INSERT INTO projects (project_link, project_host, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/TriviaGame", "https://mcarreon.github.io/TriviaGame", "76'ers Trivia", "Assignment", "2018-6-21","The challenge of this assignment was to use jQuery to create a timed trivia game with 30 seconds on the clock, featuring randomized questions with a random order. The questions are from the 00-01 era.", NOW(), NOW());
