INSERT INTO projects (project_link, project_host, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/Beer-Detectives", "https://mcarreon.github.io/Beer-Detectives", "Beer-Detectives", "Group Project", "2018-7-19","First group project, devloped with two other people, hayk715 and helenpastras on GitHub, over a 2 week period of time. We created a beer browser, which allows you to save your favorite beers and view their brewery location. The site also has a section for beer and food pairings. We used two APIs: Untappd and Google Maps.", NOW(), NOW());
INSERT INTO projects (project_link, project_host, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/train-scheduler", "https://mcarreon.github.io/train-scheduler", "Train Scheduler", "Assignment", "2018-7-20","The challenge of this assignment was to use jQuery and Moment to create a tracked list of train routes, as well as their current arrival times.", NOW(), NOW());
INSERT INTO projects (project_link, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/LIRI", "LIRI", "Assignment", "2018-7-31","The challenge of this app was to create a Node CLI interface that accesses 3 APIs: Twitter, Spotify, and Omdb. The CLI has 4 commands that accept a second paramater as a query. The user can also insert a command in the random.txt file.", NOW(), NOW());
INSERT INTO projects (project_link, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/CLI-Word-Game", "Node CLI Hangman", "Assignment", "2018-8-7","The challenge of this assignment was to create a Node CLI hangman type game. I used inquirer to handle the CLI and tried to adhere to OOP principles.", NOW(), NOW());
INSERT INTO projects (project_link, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/CLI-Bamazon", "Node CLI Bamazon", "Assignment", "2018-8-25","The challenge of this app was to use Inquirer and Node to create a customer and manager application for a mock store. The customer can purchase items, while the manager can restock items and modify specific items.", NOW(), NOW());
INSERT INTO projects (project_link, project_host, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/FriendFinder", "https://still-citadel-43398.herokuapp.com/", "Friend Finder", "Assignment", "2018-8-26","The challenge of the assignment was to create an application mainly using Node and Express Servers. It also uses a few node packages like Express, Body-Parser, and Path. The application, FriendFinder, matches the user to their perfect match using a quick and fully comprehensive 10 question survey.", NOW(), NOW());
INSERT INTO projects (project_link, project_host, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/Eat-A-Burger", "https://serene-forest-19093.herokuapp.com/", "Eat Da Burger", "Assignment", "2018-9-7","The challenge for this application was to use Express, Handlebars, and MySql to create an interactive database. Jaws_DB handles the server database. The application achieves this by allowing the user to create and eat burgers!", NOW(), NOW());
INSERT INTO projects (project_link, project_host, project_name, project_type, project_creation_date, project_desc, createdAt, updatedAt) VALUES ("https://github.com/mcarreon/Eat-da-sequelized-burger", "https://obscure-citadel-61473.herokuapp.com/", "Eat Da Sequelized Burger", "Assignment", "2018-9-13","The challenge of this assignment was to update the previous burger application to use Sequelize to handle all MySql interactions. It also has an added feature of tracking the user that makes the burger based on session.", NOW(), NOW());
